function toast(e){if("string"==typeof e&&(e={msg:e}),e.msg){var s=document.createElement("div");switch(s.id="toastId",s.classList.add("toast","in"),e.type){case"success":s.innerHTML=`<i class="iconfont icon-success"></i><p class="text">${e.msg}</p>`;break;case"error":s.innerHTML=`<i class="iconfont icon-error"></i><p class="text">${e.msg}</p>`;break;case"loading":s.innerHTML=`<i class="iconfont icon-loading"></i><p class="text">${e.msg}</p>`;break;default:s.innerHTML=`<p class="text">${e.msg}</p>`}var t=document.getElementById("toastId");if(null==t){document.body.appendChild(s),(t=document.getElementById("toastId")).classList.add("in");var n=setTimeout((()=>{t.classList.remove("in"),clearInterval(n),document.body.removeChild(s)}),e.time||2e3)}}else console.error("text 不能为空!")}
